#!/usr/bin/env python

primer_list = list()

primer_dict = {
"R":["A","G"],
"Y":["C","T"],
"S":["G","C"],
"W":["A","T"],
"K":["G","T"],
"M":["A","C"],
"B":["C","G","T"],
"D":["A","G","T"],
"H":["A","C","T"],
"V":["A","C","G"],
"N":["A","T","C","G"],
".":[" "],
"-":[" "] 
}
primer_string = list()
primer_string.append("DTTTCTACTATTTATGARATTCC")
primer_string.append("GCDATGTTTTGTAATGTTG")

def explode(p_primer_string):
	found_count = 0
	for from_char in primer_dict:
		if (p_primer_string.find(from_char) != -1):
			found_count = found_count + 1
	if ( found_count == 0 ):
		primer_list.append(p_primer_string)
	else:
		for from_char in primer_dict:
			if ( p_primer_string.find(from_char) != -1):
				for to_char in primer_dict[from_char]:
					explode(p_primer_string.replace(from_char,to_char,1))	



